package com.aurionpro.tdd;

public class AreaOfShape {

	public double calculateAreaOfTriangle(int length, int breadth, int height) {
		// TODO Auto-generated method stub
		return 0.5*length*breadth*height;
	}

	public double calculateAreaOfCircle(int radius) {
		// TODO Auto-generated method stub
		return 3.14*radius*radius;
	}

	public double calculateAreaOfSquare(int side) {
		// TODO Auto-generated method stub
		return side*side;
	}

}
